import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {
  Box,
  Typography,
  Grid,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Button,
  Pagination,
  CircularProgress,
} from '@mui/material';
import { Search as SearchIcon, Clear as ClearIcon } from '@mui/icons-material';

import { fetchTenders, setFilters, clearFilters, setPage } from '../store/slices/tendersSlice';
import TenderCard from '../components/tenders/TenderCard';

const TendersPage = () => {
  const dispatch = useDispatch();
  const { tenders, loading, pagination, filters, categories, countries } = useSelector((state) => state.tenders);

  useEffect(() => {
    dispatch(fetchTenders({ ...filters, page: pagination.page }));
  }, [dispatch, filters, pagination.page]);

  const handleSearchChange = (event) => {
    dispatch(setFilters({ search: event.target.value }));
  };

  const handleCountryChange = (event) => {
    dispatch(setFilters({ country: event.target.value }));
  };

  const handleCategoryChange = (event) => {
    dispatch(setFilters({ category: event.target.value }));
  };

  const handleStatusChange = (event) => {
    dispatch(setFilters({ status: event.target.value }));
  };

  const handleClearFilters = () => {
    dispatch(clearFilters());
  };

  const handlePageChange = (event, value) => {
    dispatch(setPage(value));
  };

  return (
    <Box>
      <Typography variant="h4" component="h1" gutterBottom>
        Browse Tenders
      </Typography>

      {/* Filters */}
      <Box sx={{ mb: 4, p: 3, bgcolor: 'background.paper', borderRadius: 2 }}>
        <Grid container spacing={2} alignItems="center">
          <Grid item xs={12} sm={6} md={3}>
            <TextField
              fullWidth
              label="Search tenders"
              value={filters.search}
              onChange={handleSearchChange}
              InputProps={{
                startAdornment: <SearchIcon sx={{ mr: 1, color: 'text.secondary' }} />,
              }}
            />
          </Grid>
          <Grid item xs={12} sm={6} md={2}>
            <FormControl fullWidth>
              <InputLabel>Country</InputLabel>
              <Select
                value={filters.country}
                label="Country"
                onChange={handleCountryChange}
              >
                <MenuItem value="">All Countries</MenuItem>
                {countries.map((country) => (
                  <MenuItem key={country} value={country}>
                    {country}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={12} sm={6} md={2}>
            <FormControl fullWidth>
              <InputLabel>Category</InputLabel>
              <Select
                value={filters.category}
                label="Category"
                onChange={handleCategoryChange}
              >
                <MenuItem value="">All Categories</MenuItem>
                {categories.map((category) => (
                  <MenuItem key={category} value={category}>
                    {category}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={12} sm={6} md={2}>
            <FormControl fullWidth>
              <InputLabel>Status</InputLabel>
              <Select
                value={filters.status}
                label="Status"
                onChange={handleStatusChange}
              >
                <MenuItem value="">All Status</MenuItem>
                <MenuItem value="Open">Open</MenuItem>
                <MenuItem value="Closed">Closed</MenuItem>
                <MenuItem value="Pending">Pending</MenuItem>
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={12} sm={6} md={3}>
            <Button
              variant="outlined"
              startIcon={<ClearIcon />}
              onClick={handleClearFilters}
              fullWidth
            >
              Clear Filters
            </Button>
          </Grid>
        </Grid>
      </Box>

      {/* Results */}
      {loading ? (
        <Box sx={{ display: 'flex', justifyContent: 'center', py: 4 }}>
          <CircularProgress />
        </Box>
      ) : (
        <>
          <Box sx={{ mb: 3 }}>
            <Typography variant="body1" color="text.secondary">
              Showing {tenders.length} of {pagination.total} tenders
            </Typography>
          </Box>

          <Grid container spacing={3}>
            {tenders.map((tender) => (
              <Grid item xs={12} md={4} key={tender._id}>
                <TenderCard tender={tender} />
              </Grid>
            ))}
          </Grid>

          {/* Pagination */}
          {pagination.pages > 1 && (
            <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
              <Pagination
                count={pagination.pages}
                page={pagination.page}
                onChange={handlePageChange}
                color="primary"
                size="large"
              />
            </Box>
          )}
        </>
      )}
    </Box>
  );
};

export default TendersPage;
